import React, { useState } from "react";
import "./App.css";
import img from "./space.png";

export default function App() {
  const [showHome, setShowHome] = useState(true);
  const [showWorld, setShowWorld] = useState(false);
  return (
    <div className="App">
      {showHome && (
        <header id="App-header" style={{ backgroundImage: `url(${img})` }}>
          <div className="App-header-title">DISCOVERY</div>
          <div className="App-header-item-row">
            <div
              className="App-header-item"
              onClick={() => {
                document.getElementById("App-header").style.animation =
                  "1.2s fadeOutLeft ease-out";
                setTimeout(function () {
                  setShowHome(false);
                  setShowWorld(true);
                }, 1200);
              }}
            >
              The World
            </div>
            <div className="App-header-item">Language</div>
            <div className="App-header-item">Stories</div>
            <div className="App-header-item">Creative</div>
          </div>
        </header>
      )}
      {showWorld && (
        <div className="App-world-experience">
          <div className="App-header-title">THE WORLD</div>
          <img
            className="App-home-button"
            src="https://img.icons8.com/pastel-glyph/128/000000/home.png"
            alt="home"
          />
        </div>
      )}
    </div>
  );
}
