import React from "react";
export function CoursePage(props) {
  return (
    props.showPage && (
      <div className={`App-${props.class}-experience`}>
        <div className="App-experience-title">
          {`${props.currentPage}`.toUpperCase()}
        </div>
        <img
          className="App-home-button"
          src="https://img.icons8.com/pastel-glyph/128/000000/home.png"
          alt="home"
          onClick={() => {
            props.setSubject(false);
            props.setHome(true);
          }}
        />
        <SpacePage class={props.class} />
      </div>
    )
  );
}

function SpacePage(props) {
  return (
    <div className="App-space-experience-content">
      {props.class === "space" && (
        <div>
          <div className="App-space-experience-usp">
            Ever looked at the sky and wondered where it all came from?
          </div>
          <div className="App-space-experience-media">
            <MediaGroup
              class={props.class}
              items={[
                { title: "Celestial Objects", data: [] },
                { title: "The Solar System", data: [] },
              ]}
            />
          </div>
        </div>
      )}
    </div>
  );
}

function MediaGroup(props) {
  return (
    <div className={`App-${props.class}-experience-media`}>
      {props.items.map((item, index) => (
        <div className="App-experience-media-item">
          <div className="App-experience-media-title">{item["title"]}</div>
        </div>
      ))}
    </div>
  );
}
