import React from "react";

export function MediaGroup(props) {
  if (props.items !== []) {
    const data = props.items;
    return (
      <div className={`App-${props.class}-experience-media`}>
        {data.map((list, num) => (
          <div className="App-experience-list" key={num}>
            <div className="App-experience-list-meta">
              <div className="App-experience-list-title">{list["title"]}</div>
              <div className="App-experience-list-background">
                {list["background"]}
              </div>
            </div>
            <div className="App-experience-list-videos">
              {list["videos"].map((item, index) => (
                <div className="App-experience-media-item" key={index}>
                  <img
                    className="App-experience-media-thumbnail"
                    src={item["thumbnail"]}
                    alt="video-thumbnail"
                    onClick={() => props.setOverlayVideo(item["link"])}
                    // onHover=
                  />
                  <div className="App-experience-media-title">
                    {item["title"]}
                  </div>
                  <div className="App-experience-media-description">
                    {item["description"]}
                  </div>
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>
    );
  }
}

class Player {
  constructor(src) {
    this.audio.src = src;
  }

  getInitialState() {
    return {
      songs: [],
      playing: false,
    };
  }

  playSong() {
    console.log("play");
    this.setState({ playing: true });
    this.audio.src = this.audio.src;
    this.audio.play();
  }

  pauseSong() {
    console.log("pause");
    this.setState({ playing: false });
    this.audio.pause();
  }
}
