import React from "react";

export class BackgroundMusic extends React.Component {
  constructor(src) {
    super(src);
    this.audio = new Audio();
    this.audio.loop = true;
    this.audio.src = src;
  }
  componentDidMount() {
    this.audio.play();
  }

  playSong() {
    console.log("play");
    this.setState({ playing: true });
    this.audio.play();
  }

  pauseSong() {
    console.log("pause");
    this.setState({ playing: false });
    this.audio.pause();
  }

  render() {
    return (
      <p onClick={this.state.playing ? this.pauseSong : this.playSong}>
        Play/Pause
      </p>
    );
  }
}
