import React, { useState } from "react";
import { SpacePage } from "./oceanEverything/SpacePage";
import { StoriesPage } from "./moralEducation/StoriesPage";

export function CoursePage(props) {
  const [overlayVideo, setOverlayVideo] = useState("");
  return (
    props.showPage && (
      <>
        <MediaOverlay video={overlayVideo} setOverlayVideo={setOverlayVideo} />
        <div className={`App-${props.class}-experience`}>
          <div className="App-experience-title">
            {`${props.currentPage}`.toUpperCase()}
          </div>
          <img
            className="App-home-button"
            src="https://img.icons8.com/pastel-glyph/128/000000/home.png"
            alt="home"
            onClick={() => {
              props.setSubject(false);
              props.setHome(true);
            }}
          />
          <SpacePage class={props.class} />
          <StoriesPage class={props.class} setOverlayVideo={setOverlayVideo} />
        </div>
      </>
    )
  );
}

function MediaOverlay(props) {
  return (
    props.video !== "" && (
      <div
        className="App-experience-overlay"
        onKeyDown={() => console.log("you keyed down")}
        onClick={() => props.setOverlayVideo("")}
      >
        <iframe
          className="App-experience-overlay-video"
          src={props.video}
          frameborder="0"
          allow="accelerometer; encrypted-media; gyroscope;"
          allowfullscreen="allowfullscreen"
          title="Enlarged Video"
        ></iframe>
        />
      </div>
    )
  );
}
