import React from "react";

function playSong(props) {
  console.log("play");
  const player = document.getElementById(`${props.class}BgMusic`);
  player.play();
}

export function MediaOverlay(props) {
  return (
    props.video !== "" && (
      <div
        className="App-experience-overlay"
        onClick={() => {
          props.setOverlayVideo("");
          playSong(props.class);
        }}
      >
        <iframe
          className="App-experience-overlay-video"
          src={props.link.video}
          frameBorder="0"
          allow="accelerometer; encrypted-media; gyroscope;"
          allowFullScreen="allowfullscreen"
          title="Enlarged Video"
        ></iframe>
        />
      </div>
    )
  );
}
