import React from "react";

function playSong(classN, src) {
  console.log("play");
  const player = document.getElementById(`${classN}BgMusic`);
  player.play();
}

function pauseSong(classN, src) {
  console.log("pause");
  console.log(classN);
  const player = document.getElementById(`${classN}BgMusic`);
  player.autoplay = false;
  console.log(player.autoplay);
  player.pause();
  player.src = src;
}

export function MediaOverlay(props) {
  return (
    props.video !== "" && (
      <div
        className="App-experience-overlay"
        onClick={() => {
          props.setOverlayVideo("");
          playSong(props.video.class, props.video.src);
        }}
      >
        <iframe
          className="App-experience-overlay-video"
          src={props.video.link}
          frameBorder="0"
          allow="accelerometer; encrypted-media; gyroscope;"
          allowFullScreen="allowfullscreen"
          title="Enlarged Video"
          onClick={() => pauseSong(props.class, props.src)}
        ></iframe>
        />
      </div>
    )
  );
}
