import React, { useState } from "react";
import "./App.css";
import spaceBG from "./space.png";

export default function App() {
  const [home, setHome] = useState(true);
  const [currentPage, setCurrentPage] = useState("home");
  const [space, setSpace] = useState(false);
  const [earth, setEarth] = useState(false);

  const pageStates = {
    Space: { state: space, setState: setSpace },
    "Our Planet": { state: earth, setState: setEarth },
  };

  return (
    <div id="App">
      {home && (
        <header
          id="App-header"
          style={{
            background: `url(${spaceBG}) rgba(0,0,0,45%)`,
            backgroundBlendMode: "darken",
            backgroundSize: "cover",
          }}
        >
          <div className="App-header-title">DISCOVERY</div>
          <div className="App-header-item-row">
            <SubjectItem
              setHome={setHome}
              setSubject={setSpace}
              setPage={setCurrentPage}
              subjectTitle="Space"
            />
            <SubjectItem
              setHome={setHome}
              setSubject={setEarth}
              setPage={setCurrentPage}
              subjectTitle="Our Planet"
            />
            <div className="App-header-item">Language</div>
            <div className="App-header-item">Stories</div>
            <div className="App-header-item">Creative</div>
          </div>
        </header>
      )}
      console.log(currentPage)
      <CoursePage
        showPage={pageStates[`${currentPage}`]["state"]}
        class={`${pageStates[`${currentPage}`]["state"]}`}
        setHome={setHome}
        setSubject={pageStates[`${currentPage}`]["setState"]}
        currentPage={currentPage}
      />
    </div>
  );
}

function SubjectItem(props) {
  return (
    <div
      className="App-header-item"
      onClick={() => {
        props.setHome(false);
        props.setSubject(true);
        props.setCurrentPage(props.subjectTitle);
      }}
    >
      {props.subjectTitle}
    </div>
  );
}

function CoursePage(props) {
  return (
    props.showPage && (
      <div className={`App-${props.class}-experience`}>
        <div className="App-experience-title">
          {`${props.currentPage}`.toUpperCase()}
        </div>
        <img
          className="App-home-button"
          src="https://img.icons8.com/pastel-glyph/128/000000/home.png"
          alt="home"
          onClick={() => {
            props.setSubject(false);
            props.setHome(true);
          }}
        />
      </div>
    )
  );
}
