export class BackgroundMusic {
  constructor(src) {
    this.audio = new Audio();
    this.audio.loop = true;
    this.audio.src = src;
  }

  getInitialState() {
    return {
      songs: [],
      playing: false,
    };
  }

  componentDidMount() {
    this.audio.play();
  }

  playSong() {
    console.log("play");
    this.setState({ playing: true });
    this.audio.play();
  }

  pauseSong() {
    console.log("pause");
    this.setState({ playing: false });
    this.audio.pause();
  }
}
