import React, { useState } from "react";
import "../static/styles/App.css";
import { CoursePage } from "./components/CoursePage";
import { SubjectItem } from "./components/SubjectItem";

export default function App() {
  const [home, setHome] = useState(true);
  const [currentPage, setCurrentPage] = useState("home");
  const [space, setSpace] = useState(false);
  const [earth, setEarth] = useState(false);

  const pageStates = [
    {
      state: space,
      setState: setSpace,
      class: "space",
      title: "Space",
    },
    {
      state: earth,
      setState: setEarth,
      class: "earth",
      title: "Our Planet",
    },
  ];

  return (
    <div id="App">
      {home && (
        <header id="App-header">
          <div className="App-header-title">DISCOVERY</div>
          <div className="App-header-item-row">
            {pageStates.map((item, index) => (
              <SubjectItem
                setHome={setHome}
                setSubject={item["setState"]}
                setPage={setCurrentPage}
                subjectTitle={item["title"]}
              />
            ))}
            <div className="App-header-item">Language</div>
            <div className="App-header-item">Stories</div>
            <div className="App-header-item">Creative</div>
          </div>
        </header>
      )}
      {currentPage !== "home" && (
        <CoursePage
          showPage={
            pageStates.filter(function (item, currentPage) {
              return item["title"] === currentPage;
            })["state"]
          }
          class={
            pageStates.filter(function (item, currentPage) {
              return item["title"] === currentPage;
            })["class"]
          }
          setHome={setHome}
          setSubject={
            pageStates.filter(function (item, currentPage) {
              return item["title"] === currentPage;
            })["setState"]
          }
          currentPage={currentPage}
        />
      )}
    </div>
  );
}
