import React, { useState } from "react";
import "../static/styles/App.css";
import { GetPageMeta } from "../constants/index";
import { CoursePage } from "./components/CoursePage";
import { SubjectItem } from "./components/SubjectItem";

export default function App() {
  const [home, setHome] = useState(true);
  const [currentPage, setCurrentPage] = useState("home");
  const [space, setSpace] = useState(false);
  const [earth, setEarth] = useState(false);
  const [language, setLanguage] = useState(false);

  const pageStates = [
    {
      state: space,
      setState: setSpace,
      class: "space",
      title: "Space",
    },
    {
      state: earth,
      setState: setEarth,
      class: "earth",
      title: "Our Planet",
    },
    {
      state: language,
      setState: setLanguage,
      class: "language",
      title: "Language",
    },
  ];

  return (
    <div id="App">
      {home && (
        <header id="App-header">
          <div className="App-header-title">DISCOVERY</div>
          <div className="App-header-item-row">
            {pageStates.map((item, index) => (
              <SubjectItem
                key={index}
                setHome={setHome}
                setSubject={item["setState"]}
                setPage={setCurrentPage}
                subjectTitle={item["title"]}
              />
            ))}
            <div className="App-header-item">Stories</div>
            <div className="App-header-item">Creative</div>
          </div>
        </header>
      )}
      {currentPage !== "home" && (
        <CoursePage
          showPage={GetPageMeta(pageStates, currentPage, "state")}
          class={GetPageMeta(pageStates, currentPage, "class")}
          setHome={setHome}
          setSubject={GetPageMeta(pageStates, currentPage, "setState")}
          currentPage={currentPage}
        />
      )}
    </div>
  );
}
